const puppeteer = require('puppeteer');

// Leggi l'input dal nodo precedente (rimuovi eventuali spazi nel nome della chiave)
const url = $input.first().json["startingUrl "]  || 'https://default-url.it';
const versionePrevista=$input.first().json.versionePrevista;

// 1) Se non esiste ancora, lancio Chromium e lo memorizzo in global.browser
//nei nodi successivi questa parte di creazione non serve
if (!global.browser) {
  global.browser = await puppeteer.launch({
    executablePath: '/usr/bin/chromium-browser',
    headless: true,
    args: ['--no-sandbox', '--disable-setuid-sandbox'],
  });
}

// 2) Creo la variabile locale browser per uniformità con i nodi “Run” e “Close”
const browser = global.browser;

page = await browser.newPage();

// 1) Carico la pagina e aspetto "network idle"
await page.goto(url, {
  waitUntil: 'networkidle0',
  timeout: $input.first().json.browserTimeoutInMillSeconds
});

// 2) Aspetto che nel body compaia il pattern Vx.y.z
await page.waitForFunction(
  () => /V\d+\.\d+\.\d+/.test(document.body.innerText),
  { timeout: $input.first().json.browserTimeoutInMillSeconds }
);

// 3) Estraggo la versione con un unico evaluate che usa document.evaluate()
const versioneTrovata = await page.evaluate(() => {
  const xpath = "//p[contains(text(),'V') and contains(text(),'.')]";
  const node = document
    .evaluate(
      xpath,
      document,
      null,
      XPathResult.FIRST_ORDERED_NODE_TYPE,
      null
    )
    .singleNodeValue;
  return node ? node.textContent.trim() : 'Versione non trovata';
});

await browser.close(); //questo non sarà da fare quando si eseguiranno gli altri nodi

// 6) Ritorna il risultato confrontando con la versione prevista
return [
  {
    json: {
      url,
      versionePrevista,
      versioneTrovata: versioneTrovata || 'Versione non trovata',
      isVersioneCorretta: versionePrevista === versioneTrovata,
    },
  },
]
